func (gq *GameQuery) loadNumericalStatDescriptions(ctx context.Context, query *NumericalStatDescriptionQuery, nodes []*Game, init func(*Game), assign func(*Game, *NumericalStatDescription)) error {
	edgeIDs := make([]driver.Value, len(nodes))
	byID := make(map[guidgql.GUID]*Game)
	nids := make(map[guidgql.GUID]map[*Game]struct{})
	for i, node := range nodes {
		edgeIDs[i] = node.ID
		byID[node.ID] = node
		if init != nil {
			init(node)
		}
	}
	query.Where(func(s *sql.Selector) {
		joinT := sql.Table(game.NumericalStatDescriptionsTable)
		s.Join(joinT).On(s.C(numericalstatdescription.FieldID), joinT.C(game.NumericalStatDescriptionsPrimaryKey[0]))
		s.Where(sql.InValues(joinT.C(game.NumericalStatDescriptionsPrimaryKey[1]), edgeIDs...))
		columns := s.SelectedColumns()
		s.Select(joinT.C(game.NumericalStatDescriptionsPrimaryKey[1]))
		s.AppendSelect(columns...)
		s.SetDistinct(false)
	})
	if err := query.prepareQuery(ctx); err != nil {
		return err
	}
	neighbors, err := query.sqlAll(ctx, func(_ context.Context, spec *sqlgraph.QuerySpec) {
		assign := spec.Assign
		values := spec.ScanValues
		spec.ScanValues = func(columns []string) ([]any, error) {
			values, err := values(columns[1:])
			if err != nil {
				return nil, err
			}
			return append([]any{new(guidgql.GUID)}, values...), nil
		}
		spec.Assign = func(columns []string, values []any) error {
			outValue := *values[0].(*guidgql.GUID)
			inValue := *values[1].(*guidgql.GUID)
			if nids[inValue] == nil {
				nids[inValue] = map[*Game]struct{}{byID[outValue]: {}}
				return assign(columns[1:], values[1:])
			}
			nids[inValue][byID[outValue]] = struct{}{}
			return nil
		}
	})
	if err != nil {
		return err
	}
	for _, n := range neighbors {
		nodes, ok := nids[n.ID]
		if !ok {
			return fmt.Errorf(`unexpected "numerical_stat_descriptions" node returned %v`, n.ID)
		}
		for kn := range nodes {
			assign(kn, n)
		}
	}
	return nil
}
